=PATH/PATH/{{
  (n => {
    n = $('GoogleDrive-upload-file').item.json.name || $json.name || 'invoice';
    n = n.replace(/[\x00-\x1F\x7F]/g, '');
    n = n.replace(/[:<>\"/\\|?*]/g, '-');
    n = n.replace(/\s+/g, ' ').replace(/[\. ]+$/, '');
    return n.toLowerCase().endsWith('.pdf') ? n : n + '.pdf';
  })()
}}
